import React, {useState} from 'react';

export default initialVal => {
    const [val, setValue] = useState(initialVal);
    const handleChange = e=>{
        setValue(e.target.value);
    };
    const reset =() =>{
        setValue("");
    };
    return [val, handleChange, reset];
};
