import React, {useState} from 'react';
import Typography from "@material-ui/core/Typography";
import Paper from "@material-ui/core/Paper";
import AppBar from "@material-ui/core/AppBar";
import ToolBar from "@material-ui/core/Toolbar";
import Grid from "@material-ui/core/Grid";
import TodoList from './TodoList';
import TodoForm from './TodoForm';
function TodoApp(){
    const initialTodos =[
        {id: 1, task:'Do wash', completed: true},
        {id: 2, task:'Eat', completed: true},
        {id: 3, task:'Tennis', completed: false},

    ]
    const [todos, setTodos] = useState(initialTodos);
    const addTodo = (newTodoText) => {
        setTodos([...todos, {id: 5, task: newTodoText, completed: false}]);
    };
    const removeTodo = todoId =>{
        const updatedTodos = todos.filter(todo => todo.id !== todoId);
    };
    return (
        <Paper style = {{padding: 0, margin: 0, height: "100vh", backgroundColor: "white" }} elevation={0}>

            <AppBar color='primary' position='static' style={{height:"64px" }}>
                <ToolBar>
                    <Typography color='inherit'>
                        TODOs WITH HOOLS
                    </Typography>
                </ToolBar>
            </AppBar>
            <Grid container justify='center' style={{marginTop: "1rem"}}>
                <Grid item xs={11} md={8} lg={4}>
                    <TodoForm addTodoss={addTodo} />
                    <TodoList todo = {todos}  removeTodo ={removeTodo} />
                 </Grid>
            </Grid>

        </Paper>
    );
}
export default TodoApp;
